import React, { useState, useEffect } from "react";
import { TextField, Button } from "@material-ui/core";
import "./App.css";
import Todo from "./components/Todo/Todo.js";
import db from './firebase';

const App = () => {
  const [todo, setTodo] = useState(["deep"]);
  const [input, setInput] = useState("");

  console.log(todo);

  const handleAdd = (event) => {
    event.preventDefault();

    db.collection('deeptodo').add({
      todo: input
    })

    setInput("");
  };

  useEffect(() => {

    db.collection('deeptodo').onSnapshot(snapshot => {
      setTodo(snapshot.docs.map(doc => ({id: doc.id, todo: doc.data().todo})))
    })


  }, [])

  return (
    <div>
      <div className="todo-container">
        <h2 className="font-heading">
          Todo App <span>🚩</span>
        </h2>
        <form>
          <TextField
            id="standard-basic"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            autoComplete="off"
            label="✍ Add a Task"
          />
          <Button
            type="submit"
            onClick={handleAdd}
            disabled={!input}
            variant="contained"
            color="secondary"
          >
            Add
          </Button>
        </form>
        {todo.map((td) => (
          <Todo todo={td}> </Todo>
        ))}
      </div>
    </div>
  );
};

export default App;
