import React, {useState} from 'react';
import {TextField, Button} from '@material-ui/core';
import './App.css';

const App = () => {

  const [todo, setTodo] = useState([]);
  const [input, setInput] = useState('');

  console.log(todo);

  const handleAdd = () => {
    const newTodo = [...todo, input];
    setTodo(newTodo);
  }

  return (
    <div>

      <div className = "todo-container">
        <h2 className = "font-heading">Todo App <span>🚩</span></h2>
        <TextField id="standard-basic" value = {input} onChange = {e => setInput(e.target.value)} label="✍ Add a Task" />
        <Button onClick = {handleAdd} variant="contained" color="secondary">
          Add
        </Button>
      </div>


    </div>
  );
}

export default App;
