import React, {useState} from 'react';
import {TextField, Button} from '@material-ui/core';
import './App.css';
import Todo from './components/Todo/Todo.js'

const App = () => {

  const [todo, setTodo] = useState(['deep', 'meoow']);
  const [input, setInput] = useState('');

  console.log(todo);

  const handleAdd = () => {
    const newTodo = [...todo, input];
    setTodo(newTodo);
  }

  return (
    <div>

      <div className = "todo-container">
        <h2 className = "font-heading">Todo App <span>🚩</span></h2>
        <TextField id="standard-basic" value = {input} onChange = {e => setInput(e.target.value)} autoComplete = "off" label="✍ Add a Task" />
        <Button onClick = {handleAdd} disabled = {!input} variant="contained" color="secondary">
          Add
        </Button>
        {

          todo.map(td => <Todo> text={td} </Todo>)
        }
      </div>


    </div>
  );
}

export default App;
